mapper.select:
		
		select * from(
		select rownum as rnum,c.*
  		from(
        select  no,title,cover,get_date as getDate,
        member_no from gifticon where member_no=#{memberNo}
     			 order by getDate desc
        		)c)
        	where (#{pageNo}-1)*5+1 <= rnum  and rnum <= #{pageNo}*5
